#!/anaconda3/bin/python3
#http://rosalind.info/problems/hamm/
#This script takes two input sequences of equal length
#and prints the number of mismatches between the seqs

subject = ''
query = ''

seq_length = len(subject)

match = 0
mismatch = 0

for letter in range(seq_length):
    if subject[letter] == query[letter]:
        match += 1
    else:
        mismatch += 1
    

assert match + mismatch == seq_length, "Counts are off!"
print(mismatch)