#!/anaconda3/bin/python3
# http://rosalind.info/problems/ba1a/
# sliding window along sequence in text


def COUNT(text, pattern):
    count = 0
    for i in range(0, (len(text) - len(pattern)+1)):
        if text[i:i+len(pattern)] == pattern:
            count += 1
    return count


#original sample dataset
# sequence = 'GCGCG'
# kmer = 'GCG'
#output should be 2

#debugging inputs
# sequence = 'ACGTACGTACGT'
# kmer = 'CG'
#output should be 3

sequence = ''
kmer = 'TGAACATTG'

print(COUNT(sequence, kmer))


